#| echo: false # 自动生成论文列表 papers &lt;- data.frame( Year = c(2025, 2024, 2024, 2023), Authors = c(“Zhang M, Wang Q, et al.”, “Liu F, Zhang M, et al.”, “Chen M, Li H”, “Yang F, Zhang M”), Title = c( “新型生物炭复合材料对镉污染土壤的修复机制”, “江汉平原农业生态系统服务功能评估”, “秸秆基生物炭的制备及其对磷的吸附性能”, “农田排水沟渠对氮磷拦截效率研究” ), Journal = c( “环境科学, 46(5): 1234-1245”, “生态学报, 44(3): 567-578”, “农业环境科学学报, 43(2): 345-356”, “环境科学学报, 42(8): 2345-2356” ), DOI = c(“10.13227/j.hjkx.202410123”, “10.5846/stxb202310123456”, “10.11654/jaes.2023.1234”, “10.13671/j.hjkxxb.2023.0456”) )
cat(“## 发表论文”) for(i in 1:nrow(papers)){ cat(paste0( “- **“, papers\(Title[i], "**  \n",
    "  ", papers\)Authors[i],” “,” *“, papers\(Journal[i], "* (", papers\)Year[i],”) “,” [DOI:“, papers\(DOI[i], "](https://doi.org/", papers\)DOI[i],”)” )) }

